package com.konkerlabs.platform.registry.test.web.services;

import static org.mockito.Matchers.any;
import static org.mockito.Matchers.anyBoolean;
import static org.mockito.Matchers.anyString;
import static org.mockito.Mockito.doNothing;
import static org.mockito.Mockito.mock;
import static org.mockito.Mockito.when;

import java.io.ByteArrayInputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.InputStream;
import java.util.Collections;
import java.util.List;

import org.junit.After;
import org.junit.Assert;
import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.mockito.invocation.InvocationOnMock;
import org.mockito.stubbing.Answer;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.test.context.ContextConfiguration;
import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;

import com.amazonaws.util.Base64;
import com.konkerlabs.platform.registry.business.repositories.upload.AwsUploadRepository;
import com.konkerlabs.platform.registry.business.services.api.ServiceResponse;
import com.konkerlabs.platform.registry.config.CdnConfig;
import com.konkerlabs.platform.registry.test.base.BusinessLayerTestSupport;
import com.konkerlabs.platform.registry.test.base.BusinessTestConfiguration;
import com.konkerlabs.platform.registry.test.base.MongoTestConfiguration;
import com.konkerlabs.platform.registry.web.services.UploadServiceImpl;
import com.konkerlabs.platform.registry.web.services.api.UploadService;

@RunWith(SpringJUnit4ClassRunner.class)
@ContextConfiguration(classes = {
        MongoTestConfiguration.class,
        BusinessTestConfiguration.class,
        CdnConfig.class
})
public class UploadServiceTest extends BusinessLayerTestSupport {

    private static String fileName;
    private static String base64Img;

    @Autowired
    private UploadService service;

    private AwsUploadRepository awsUploadRepository;


    private static List<String> filesUsedInTheTest = Collections.emptyList();

    @Test
    public void shouldUploadBase64File() throws Exception {

        ServiceResponse<String> response =
                service.uploadBase64Img("data:image/jpeg;base64," + base64Img, true);
        Assert.assertEquals(response.isOk(), true);
        Assert.assertNotNull(response.getResult());
    }

    @Test
    public void shouldUploadInputStreamFile() throws Exception {
        InputStream is = new ByteArrayInputStream(Base64.decode(base64Img.getBytes()));
        ServiceResponse<String> response = service.upload(is, fileName, "jpg", true);
        Assert.assertNotNull(response);
    }

    public InputStream shouldDownloadPublicFile(String path) throws Exception {
        ServiceResponse<InputStream> response =
                service.downloadFile(path);
        Assert.assertNotNull(response);
        Assert.assertEquals(response.isOk(), true);
        Assert.assertNotNull(response.getResult());
        return response.getResult();
    }


    public void shouldDeleteFile(String path) {
        service.delete(path);
    }

    @After
    public void tearDown() {
        filesUsedInTheTest.stream().forEach(path -> {
            shouldDeleteFile(path);
        });
    }

    private void mockRepository() {
        try {

            awsUploadRepository = mock(AwsUploadRepository.class);
            when(awsUploadRepository.upload(anyString(), anyString(), anyString(), anyBoolean()))
                    .thenAnswer(new Answer<String>() {
                        @Override
                        public String answer(InvocationOnMock invocationOnMock) throws Throwable {
                            return "filename";
                        }
                    });
            when(awsUploadRepository.upload(any(InputStream.class), anyString(), anyString(), anyBoolean()))
                    .thenAnswer(new Answer<String>() {
                        @Override
                        public String answer(InvocationOnMock invocationOnMock) throws Throwable {
                            return "s34tst";
                        }
                    });
            when(awsUploadRepository.downloadAsBase64(anyString()))
                    .thenAnswer(new Answer<String>() {
                        @Override
                        public String answer(InvocationOnMock invocationOnMock) throws Throwable {
                            return "s34tst";
                        }
                    });
            when(awsUploadRepository.downloadFile(anyString()))
                    .thenAnswer(new Answer<InputStream>() {
                        @Override
                        public InputStream answer(InvocationOnMock invocationOnMock) throws Throwable {
                            return new FileInputStream(File.createTempFile("s34tst","jpg"));
                        }
                    });

            doNothing().when(awsUploadRepository).delete(anyString());

        } catch (Exception e){
            e.printStackTrace();
        }


    }

    @Before
    public void setUp() {
        mockRepository();
        service = new UploadServiceImpl(awsUploadRepository);
        fileName = "test";
        base64Img = "";
    }
}
